/**
 *
 * @param pCard
 * 		The card we wish to move.
 * @param pLocation
 * 		The desired location for pCard.
 * @return True if pCard can be moved to the top of pLocation. This is
only possible if its rank is immediately superior to that of the
card currently on top of the pile or, in the case of an Ace, if
the location is empty.
 * @pre pCard != null && pLocation != null.
 */
boolean canMoveTo(ca.mcgill.cs.stg.solitaire.cards.Card pCard, ca.mcgill.cs.stg.solitaire.model.FoundationPile pLocation) {
    assert (pCard != null) && (pLocation != null);
    if (isEmpty(pLocation)) {
        return pCard.rank() == ca.mcgill.cs.stg.solitaire.cards.Rank.ACE;
    } else {
        return (pCard.suit() == peek(pLocation).suit()) && (pCard.rank().ordinal() == (peek(pLocation).rank().ordinal() + 1));
    }
}

