    ca.mcgill.cs.stg.solitaire.model.Location source = find(pCard);
    if ((source instanceof ca.mcgill.cs.stg.solitaire.model.TableauPile) && (pDestination instanceof ca.mcgill.cs.stg.solitaire.model.TableauPile)) {
        aTableau.moveWithin(pCard, ((ca.mcgill.cs.stg.solitaire.model.TableauPile) (source)), ((ca.mcgill.cs.stg.solitaire.model.TableauPile) (pDestination)));
