        if (pDestination instanceof ca.mcgill.cs.stg.solitaire.model.FoundationPile) {
            aFoundations.push(pCard, ((ca.mcgill.cs.stg.solitaire.model.FoundationPile) (pDestination)));
        } else if (pDestination == ca.mcgill.cs.stg.solitaire.model.OtherLocation.DISCARD_PILE) {
            aDiscard.push(pCard);
