private static void playGame(ca.mcgill.cs.stg.solitaire.model.GameModel pModel) {
    pModel.reset();
    boolean advanced = true;
    while (advanced) {
        advanced = pModel.tryToAutoPlay();
    } 
    while (pModel.canUndo()) {
        pModel.undoLast();
    } 
    advanced = true;
    while (advanced) {
        advanced = pModel.tryToAutoPlay();
    } 
}

