private void buildLayout() {
    getChildren().clear();
    int offset = 0;
    ca.mcgill.cs.stg.solitaire.cards.CardStack stack = aModel.getTableauPile(aIndex);
    // this essentially acts as a spacer
    if (stack.isEmpty()) {
        javafx.scene.image.ImageView image = new javafx.scene.image.ImageView(ca.mcgill.cs.stg.solitaire.gui.CardImages.imageForBackOfCard());
        image.setVisible(false);
        getChildren().add(image);
        return;
    }
    for (ca.mcgill.cs.stg.solitaire.cards.Card cardView : stack) {
        final javafx.scene.image.ImageView image = new javafx.scene.image.ImageView(getImage(cardView));
        image.setTranslateY(ca.mcgill.cs.stg.solitaire.gui.CardPileView.Y_OFFSET * offset);
        offset++;
        getChildren().add(image);
        setOnDragOver(createDragOverHandler(image, cardView));
        setOnDragEntered(createDragEnteredHandler(image, cardView));
        setOnDragExited(createDragExitedHandler(image, cardView));
        setOnDragDropped(createDragDroppedHandler(image, cardView));
        if (aModel.isVisibleInTableau(cardView)) {
            image.setOnDragDetected(createDragDetectedHandler(image, cardView));
        }
    }
}

