 * Move pCard and all the cards below to pDestination.
 *
 * @param pCard
 * 		The card to move, possibly including all the cards on top of it.
 * @param pOrigin
 * 		The location of the card before the move.
 * @param pDestination
 * 		The intended destination of the card.
 * @pre this is a legal move.
 */
void moveWithin(ca.mcgill.cs.stg.solitaire.cards.Card pCard, ca.mcgill.cs.stg.solitaire.model.TableauPile pOrigin, ca.mcgill.cs.stg.solitaire.model.TableauPile pDestination) {
    assert ((pCard != null) && (pOrigin != null)) && (pDestination != null);
    assert contains(pCard, pOrigin);
    assert isVisible(pCard);
    java.util.Stack<ca.mcgill.cs.stg.solitaire.cards.Card> temp = new java.util.Stack<>();
    ca.mcgill.cs.stg.solitaire.cards.Card card = aPiles.get(pOrigin).pop();
    temp.push(card);
    while (card != pCard) {
        card = aPiles.get(pOrigin).pop();
        temp.push(card);
    } 
    while (!temp.isEmpty()) {
        aPiles.get(pDestination).push(temp.pop());
    } 
}

