/**
 *
 * @param pCards
 * 		A card stack to serialize.
 * @return A serialized version of this stack.
 */
public static java.lang.String serialize(ca.mcgill.cs.stg.solitaire.cards.CardStack pCards) {
    assert pCards != null;
    java.util.StringJoiner serialized = new java.util.StringJoiner(ca.mcgill.cs.stg.solitaire.cards.CardSerializer.SEPARATOR);
    for (ca.mcgill.cs.stg.solitaire.cards.Card card : pCards) {
        serialized.add(ca.mcgill.cs.stg.solitaire.cards.CardSerializer.cardToInt(card));
    }
    return serialized.toString();
}

