properties.sourceText:
SuitStack(ca.mcgill.cs.stg.solitaire.model.GameModel pModel, ca.mcgill.cs.stg.solitaire.model.FoundationPile pIndex) {
    aModel = pModel;
    aIndex = pIndex;
    setPadding(new javafx.geometry.Insets(ca.mcgill.cs.stg.solitaire.gui.SuitStack.PADDING));
    setStyle(ca.mcgill.cs.stg.solitaire.gui.SuitStack.BORDER_STYLE);
    final javafx.scene.image.ImageView image = new javafx.scene.image.ImageView(ca.mcgill.cs.stg.solitaire.gui.CardImages.imageForBackOfCard());
    image.setVisible(false);
    getChildren().add(image);
    aDragHandler = new ca.mcgill.cs.stg.solitaire.gui.CardDragHandler(image);
    image.setOnDragDetected(aDragHandler);
    setOnDragOver(createOnDragOverHandler(image));
    setOnDragEntered(createOnDragEnteredHandler());
    setOnDragExited(createOnDragExitedHandler());
    setOnDragDropped(createOnDragDroppedHandler());
    aModel.addListener(this);
}

@java.lang.Override
public void perform() {
    aTableau.showTop(aIndex);
    aMoves.push(this);
    notifyListeners();
}

@java.lang.Override
public boolean isLowestVisibleInTableau(ca.mcgill.cs.stg.solitaire.cards.Card pCard) {
    return aTableau.contains(pCard) && aTableau.isLowestVisible(pCard);
}

/**
 *
 * @param pCard
 * 		A card to locate.
 * @return The game location where this card currently is.
 * @pre the card is in a location where it can be found and moved.
 */
private ca.mcgill.cs.stg.solitaire.model.Location find(ca.mcgill.cs.stg.solitaire.cards.Card pCard) {
    if ((!aDiscard.isEmpty()) && (aDiscard.peek() == pCard)) {
        return ca.mcgill.cs.stg.solitaire.model.OtherLocation.DISCARD_PILE;
    }
    for (ca.mcgill.cs.stg.solitaire.model.FoundationPile index : ca.mcgill.cs.stg.solitaire.model.FoundationPile.values()) {
        if ((!aFoundations.isEmpty(index)) && (aFoundations.peek(index) == pCard)) {
            return index;
        }
    }
    for (ca.mcgill.cs.stg.solitaire.model.TableauPile index : ca.mcgill.cs.stg.solitaire.model.TableauPile.values()) {
        if (aTableau.contains(pCard, index)) {
            return index;
        }
    }
    assert false;// We did not find the card: the precondition was not met.

    return null;
}

