    button = new java.awt.Button("Help");
    button.addActionListener(new java.awt.event.ActionListener() {
        public void actionPerformed(java.awt.event.ActionEvent event) {
            showHelp();
        }
    });
    panel.add(button);
    fUpdateButton = new java.awt.Button("Simple Update");
