        return;
    }
    if (mapped(storable)) {
        writeRef(storable);
        return;
    }
    incrementIndent();
    startNewLine();
    map(storable);
    fStream.print(storable.getClass().getName());
    space();
    storable.write(this);
    space();
    decrementIndent();
}

