    int y = insets.top + fBorder.y;
    for (int i = 0; i < nmembers; i++) {
        java.awt.Component m = target.getComponent(i);
        if (m.isVisible()) {
            java.awt.Dimension d = m.getMinimumSize();
            m.setBounds(x, y, d.width, d.height);
            if (fVerticalLayout) {
