    willChange();
    int n = fPoly.npoints + 1;
    int[] xs = new int[n];
    int[] ys = new int[n];
    for (int j = 0; j < i; ++j) {
        xs[j] = fPoly.xpoints[j];
        ys[j] = fPoly.ypoints[j];
    }
    xs[i] = p.x;
    ys[i] = p.y;
    for (int j = i; j < fPoly.npoints; ++j) {
        xs[j + 1] = fPoly.xpoints[j];
        ys[j + 1] = fPoly.ypoints[j];
    }
