    }
    if (c != null) {
        fEndConnector = findConnector(e.getX(), e.getY(), c);
        if (fEndConnector != null) {
            fConnection.connectStart(fStartConnector);
            fConnection.connectEnd(fEndConnector);
            fConnection.updateConnection();
